import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import characters from "../assets/characters.json"; // Assuming you are importing this file

interface Pokemon {
  id: number;
  name: string;
  imageUrl: string;
  description: string;
  type: string;
  height: string;
  weight: string;
  abilities: string[];
  evolution: string;
  history: string;
}

const History = () => {
  const { id } = useParams<{ id: string }>();
  const [pokemon, setPokemon] = useState<Pokemon | null>(null);

  useEffect(() => {
    if (id) {
      const foundPokemon = characters.find(
        (character) => character.id === Number(id)
      );
      setPokemon(foundPokemon || null); // Set to null if not found
    }
  }, [id]);

  if (!pokemon) {
    return <div>Pok√©mon not found</div>;
  }

  return (
    <div>
      <h1>{pokemon.name}</h1>
      <p>{pokemon.history}</p>
      {/* Render other character details */}
    </div>
  );
};

export default History;
