import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { motion } from "framer-motion";
import { ArrowLeft, Clock, Info } from "lucide-react";
import "../styles/History.css";
import characters from "../assets/characters.json";
import { useTheme } from "../contexts/ThemeContext"; // Importing useTheme hook

const History = () => {
  const { id } = useParams<{ id: string }>();
  const { theme } = useTheme(); // Access the current theme

  const pokemon = characters[Number(id) - 1];

  if (!pokemon) {
    return <div>Pokemon not found</div>;
  }

  return (
    <div className={`history-container ${theme}`}>
      {" "}
      <Link to="/" className="back-link">
        <ArrowLeft className="back-icon" />
        Back to Pok√©dex
      </Link>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="pokemon-card"
      >
        <div className="pokemon-header">
          <div className="pokemon-image-container">
            <img
              className="pokemon-image"
              src={pokemon.imageUrl}
              alt={pokemon.name}
            />
          </div>
          <div className="pokemon-info">
            <div className="pokemon-type">{pokemon.type}</div>
            <h1 className="pokemon-name">{pokemon.name}</h1>
            <p className="pokemon-description">{pokemon.description}</p>
          </div>
        </div>
        <div className="history-section">
          <h2 className="section-title">
            <Clock className="section-icon" />
            Historical Background
          </h2>
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.3, duration: 0.5 }}
            className="history-text"
          >
            {pokemon.history}
          </motion.p>
        </div>
        <div className="additional-info-section">
          <h3 className="section-title">
            <Info className="section-icon" />
            Additional Information
          </h3>
          <ul className="info-list">
            <li className="info-item">
              <span className="info-label">Height:</span> {pokemon.height}
            </li>
            <li className="info-item">
              <span className="info-label">Weight:</span> {pokemon.weight}
            </li>
            <li className="info-item">
              <span className="info-label">Abilities:</span>{" "}
              {pokemon.abilities.join(", ")}
            </li>
            <li className="info-item">
              <span className="info-label">Evolution:</span> {pokemon.evolution}
            </li>
          </ul>
        </div>
      </motion.div>
    </div>
  );
};

export default History;
