import React, { useState } from "react";
/* Vendor CSS Module */
import "bootstrap/dist/css/bootstrap.min.css";
/* Vendor JS Module */
import "bootstrap/dist/js/bootstrap.bundle.min.js";
/* Vanilla CSS File */
import "./Body.css";
/* Importing JSON File */
import characters from "../assets/characters.json";

const Body = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(false);

  const forward = () => {
    setIsTransitioning(true);
    setCurrentIndex((prevIndex) => (prevIndex + 1) % characters.length);
  };

  const backward = () => {
    setIsTransitioning(true);
    setCurrentIndex(
      currentIndex === 0 ? characters.length - 1 : currentIndex - 1
    );
  };

  const pokemon = characters[currentIndex];

  // Reset transition flag after animation duration (500ms)
  useEffect(() => {
    if (isTransitioning) {
      const timeout = setTimeout(() => setIsTransitioning(false), 500);
      return () => clearTimeout(timeout);
    }
  }, [isTransitioning]);

  return (
    <div className="pokemon-character-display">
      <div className="container">
        <div className="left-fragment">
          <img
            src={pokemon.imageUrl}
            className={`pokemon ${isTransitioning ? "sliding" : ""}`}
            alt={pokemon.name}
          />
          <div className="button-group">
            <button onClick={backward}>
              <i className="bi bi-caret-left"></i>
            </button>
            <button onClick={forward}>
              <i className="bi bi-caret-right"></i>
            </button>
          </div>
        </div>
        <div className="right-fragment">
          <h2>{pokemon.name}</h2>
          <p>{pokemon.description}</p>
        </div>
      </div>
    </div>
  );
};

export default Body;
