import React, { useState } from "react";
import { Link } from "react-router-dom";
import ".Header.css";

const Header: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <nav
      className={`${
        darkMode ? "bg-gray-800 text-white" : "bg-white text-gray-800"
      } shadow-md`}
    >
      <div className="max-w-7xl mx-auto px-2 sm:px-6 lg:px-8">
        <div className="relative flex items-center justify-between h-16">
          {/* Mobile Menu Button */}
          <div className="absolute inset-y-0 left-0 flex items-center sm:hidden">
            <button
              onClick={toggleMenu}
              className="text-white focus:outline-none"
            >
              <span className="sr-only">Open main menu</span>
              <svg
                className="block h-6 w-6"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                aria-hidden="true"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M4 6h16M4 12h16M4 18h16"
                />
              </svg>
            </button>
          </div>

          {/* Navbar Links and Logo */}
          <div className="flex-1 flex items-center justify-center sm:items-stretch sm:justify-start">
            <div className="flex-shrink-0">
              <h1 className="text-xl font-bold">MyLogo</h1>
            </div>
            <div className={`sm:block ${isOpen ? "block" : "hidden"} sm:ml-6`}>
              <div className="flex space-x-4">
                <Link
                  to="/home"
                  className={`${
                    darkMode ? "text-gray-300" : "text-gray-800"
                  } hover:${
                    darkMode
                      ? "bg-gray-700 text-white"
                      : "bg-gray-200 text-black"
                  } px-3 py-2 rounded-md text-sm font-medium`}
                >
                  Home
                </Link>
                <Link
                  to="/services"
                  className={`${
                    darkMode ? "text-gray-300" : "text-gray-800"
                  } hover:${
                    darkMode
                      ? "bg-gray-700 text-white"
                      : "bg-gray-200 text-black"
                  } px-3 py-2 rounded-md text-sm font-medium`}
                >
                  Services
                </Link>
                <Link
                  to="/about"
                  className={`${
                    darkMode ? "text-gray-300" : "text-gray-800"
                  } hover:${
                    darkMode
                      ? "bg-gray-700 text-white"
                      : "bg-gray-200 text-black"
                  } px-3 py-2 rounded-md text-sm font-medium`}
                >
                  About
                </Link>
                <Link
                  to="/contact"
                  className={`${
                    darkMode ? "text-gray-300" : "text-gray-800"
                  } hover:${
                    darkMode
                      ? "bg-gray-700 text-white"
                      : "bg-gray-200 text-black"
                  } px-3 py-2 rounded-md text-sm font-medium`}
                >
                  Contact
                </Link>
              </div>
            </div>
          </div>

          {/* Dark Mode Toggle */}
          <button
            onClick={toggleDarkMode}
            className="sm:hidden text-white p-2 rounded-full focus:outline-none"
          >
            {darkMode ? (
              <svg
                className="w-6 h-6"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                aria-hidden="true"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M12 3v9m0 0l-3-3m3 3l3-3m9 3a9 9 0 11-9 9 9 9 0 019-9z"
                />
              </svg>
            ) : (
              <svg
                className="w-6 h-6"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                aria-hidden="true"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M12 3v9m0 0l-3-3m3 3l3-3m9 3a9 9 0 11-9 9 9 9 0 019-9z"
                />
              </svg>
            )}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Header;
