import React, { useState } from "react";
/* Vendor CSS Module */
import "bootstrap/dist/css/bootstrap.min.css";
/* Vendor JS Module */
import "bootstrap/dist/js/bootstrap.bundle.min.js";
/* Vanilla CSS File */
import "./Body.css";
/* Importing JSON File */
import characters from "../assets/characters.json";

const Body = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const forward = () => {
    setCurrentIndex((prevIndex) => characters[prevIndex + 1]);
  };

  const backward = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? characters.length - 1 : prevIndex - 1
    );
  };

  const currentPokemon = characters[currentIndex];

  return (
    <div className="pokemon-character-display">
      <div className="container">
        <div className="left-fragment">
          <img
            src={currentPokemon.imageUrl}
            className="pokemon"
            alt={currentPokemon.name}
          />
          <div className="button-group">
            <button className="btn btn-primary" onClick={backward}>
              Previous
            </button>
            <button className="btn btn-primary" onClick={forward}>
              Next
            </button>
          </div>
        </div>
        <div className="right-fragment">
          <h2>{currentPokemon.name}</h2>
          <p>{currentPokemon.description}</p>
        </div>
      </div>
    </div>
  );
};

export default Body;
